CREATE TABLE IF NOT EXISTS Company.Employee (id int(11) NOT NULL AUTO_INCREMENT PRIMARY KEY, employeeID varchar(255) DEFAULT NULL,firstName varchar(255) DEFAULT NULL,lastName varchar(255) DEFAULT NULL,phone varchar(255) DEFAULT NULL,city varchar(255) DEFAULT NULL,state varchar(255) DEFAULT NULL,country varchar(255) DEFAULT NULL,hireDate int(11) NOT NULL DEFAULT '0',endDate int(11) NOT NULL DEFAULT '0') ENGINE=InnoDB AUTO_INCREMENT=10 DEFAULT CHARSET=utf8;
